local addon = ...if (addon == "mer") then    addon = addon .. "\\TinyFilmEffect"endlocal botHeight = 48UIParent:SetPoint("BOTTOMRIGHT", 0, botHeight)local botSpace = CreateFrame("Frame", nil, UIParent)botSpace:SetAlpha(0.95)botSpace:SetFrameStrata("BACKGROUND")botSpace:SetFrameLevel(1)botSpace:SetHeight(botHeight)botSpace:SetPoint("BOTTOMLEFT", 0, -botHeight)botSpace:SetPoint("BOTTOMRIGHT", 0, -botHeight)botSpace.bg = botSpace:CreateTexture()botSpace.bg:SetTexture("Interface\\AddOns\\"..addon.."\\space")botSpace.bg:SetPoint("TOPLEFT")botSpace.bg:SetPoint("BOTTOMRIGHT")local shadow = CreateFrame("Frame", nil, UIParent)shadow:SetAlpha(0.8)shadow:SetFrameStrata("BACKGROUND")shadow:SetFrameLevel(0)shadow:SetPoint("TOPLEFT")shadow:SetPoint("BOTTOMRIGHT")shadow.bg = shadow:CreateTexture()shadow.bg:SetTexture("Interface\\AddOns\\"..addon.."\\shadow")shadow.bg:SetPoint("TOPLEFT")shadow.bg:SetPoint("BOTTOMRIGHT")    hooksecurefunc("UpdateUIParentRelativeToDebugMenu", function()    local topHeight = 40    if (DebugMenu and DebugMenu.IsVisible()) then		topHeight = topHeight + DebugMenu.GetMenuHeight()	end    UIParent:SetPoint("TOPLEFT", 0, -topHeight)    local topSpace = CreateFrame("Frame", nil, UIParent)    topSpace:SetAlpha(0.9)    topSpace:SetFrameStrata("BACKGROUND")    topSpace:SetFrameLevel(1)    topSpace:SetHeight(topHeight)    topSpace:SetPoint("TOPLEFT", 0, topHeight)    topSpace:SetPoint("TOPRIGHT", 0, topHeight)    topSpace.bg = topSpace:CreateTexture()    topSpace.bg:SetTexture("Interface\\AddOns\\"..addon.."\\space")    topSpace.bg:SetTexCoord(0, 1, 1, 0)    topSpace.bg:SetPoint("TOPLEFT")    topSpace.bg:SetPoint("BOTTOMRIGHT")end)