--------------------------------------- 点击地图顶部,显示/隐藏小地图-------------------------------------MinimapZoneTextButton:HookScript("OnClick", ToggleMinimap)--------------------------------------- 地圖縮小,隱藏放大縮小按鈕並支持滾輪-------------------------------------MinimapCluster:SetScale(0.85)MinimapZoomIn:Hide()MinimapZoomOut:Hide()--MiniMapTracking:Hide()Minimap:EnableMouseWheel(true)Minimap:HookScript("OnMouseWheel", function(self, zoom)    if (zoom > 0) then Minimap_ZoomIn()    else Minimap_ZoomOut() endend)--------------------------------------- 右上顯示坐標 (僅當大地圖打開的時候)---------------------------------------[[local function SetZoneText(x, y)    local zone = GetMinimapZoneText()    if (x and y and x > 0 and y > 0) then        MinimapZoneText:SetText(format("(%.1f,%.1f) ", x*100, y*100) .. zone)    else        MinimapZoneText:SetText(zone)    endendlocal function ShowMapCoords(self, elapsed)    if (WorldMapFrame:IsShown()) then        self.needsUpdate = true        SetZoneText(GetPlayerMapPosition("player"))    elseif (self.needsUpdate) then        self.needsUpdate = false        SetZoneText()    endendMinimapCluster:SetScript("OnUpdate", ShowMapCoords)]]--------------------------------------- 大地圖顯示玩家坐標,鼠標坐標-------------------------------------WorldMapFrame.playerPos = WorldMapFrame.BorderFrame:CreateFontString(nil, 'ARTWORK')WorldMapFrame.playerPos:SetFont(GameFontNormal:GetFont(), 12, 'THINOUTLINE')WorldMapFrame.playerPos:SetJustifyH('RIGHT')WorldMapFrame.playerPos:SetPoint('RIGHT', WorldMapFrameCloseButton, 'LEFT', -40, 0)WorldMapFrame.playerPos:SetTextColor(1, 0.82, 0.1)WorldMapFrame.mousePos = WorldMapFrame.BorderFrame:CreateFontString(nil, 'ARTWORK')WorldMapFrame.mousePos:SetFont(GameFontNormal:GetFont(), 12, 'THINOUTLINE')WorldMapFrame.mousePos:SetJustifyH('RIGHT')WorldMapFrame.mousePos:SetPoint('RIGHT', WorldMapFrameCloseButton, 'LEFT', -120, 0)WorldMapFrame:HookScript("OnUpdate", function(self, elapsed)    local zone = GetZoneText()    if (self.zone ~= zone) then        self.zone = zone        SetMapToCurrentZone()    end    local playerX, playerY = GetPlayerMapPosition("player")    if (playerX and playerY) then        local x, y = GetCursorPosition()        x = x / WorldMapButton:GetEffectiveScale()        y = y / WorldMapButton:GetEffectiveScale()        local centerX, centerY = WorldMapButton:GetCenter()        local width  = WorldMapButton:GetWidth()        local height = WorldMapButton:GetHeight()        local mouseX = (x - (centerX - (width/2))) / width        local mouseY = (centerY + (height/2) - y ) / height        self.playerPos:SetText(format("X:%.1f  Y:%.1f", playerX*100, playerY*100))        if (playerX == 0 or playerY == 0 --玩家沒有坐標也不顯示鼠標坐標            or x < centerX - width/2 or x > centerX + width/2 --X小於或大於邊界不顯示鼠標坐標            or y < centerY - height/2 or y > centerY + height/2) then --Y小於或大於邊界不顯示鼠標坐標            self.mousePos:SetText("")        else            self.mousePos:SetText(format("X:%.1f  Y:%.1f", mouseX*100, mouseY*100))        end    endend)