--------------------------------------- Tooltip美化-------------------------------------local function AeroTooltip(...)    local frame, mask    for i = 1, select("#", ...) do        frame = _G[select(i, ...)]        if (frame) then            mask = frame:CreateTexture("AeroTooltipMask")            mask:SetTexture("Interface\\Tooltips\\UI-Tooltip-Background")            mask:SetPoint("TOPLEFT", frame, "TOPLEFT", 4, -4)            mask:SetPoint("BOTTOMRIGHT", frame, "TOPRIGHT", -4, -30)            mask:SetBlendMode("ADD")            mask:SetGradientAlpha("VERTICAL", 0, 0, 0, 0, 1, 1, 1, 0.76)        end    endend--AeroTooltip("GameTooltip")--------------------------------------- Tooltip顯示法術和物品的ID-------------------------------------local function ShowTooltipIdInfo(self, id, label)    local text, found    for i = 2, self:NumLines() do        text = _G[self:GetName() .. "TextLeft" .. i]:GetText() or ""        if (strfind(text, id)) then            found = true            break        end    end    if (not found) then        self:AddLine(" ")        self:AddLine(label..id, 0.25, 0.8, 0.6)        self:Show()    endendhooksecurefunc("SetItemRef", function(link)    local _, id = strsplit(":", link or "")    if (id) then        ShowTooltipIdInfo(ItemRefTooltip, id, "ItemID: ")    endend)GameTooltip:HookScript("OnTooltipSetSpell", function(self)    local id = select(3, self:GetSpell())    if (id) then        ShowTooltipIdInfo(self, id, "SpellID: ")    endend)GameTooltip:HookScript("OnTooltipSetItem", function(self)    local item = select(2,self:GetItem())    if (item) then        local _, id = strsplit(":", item)        if (id) then            ShowTooltipIdInfo(self, id, "ItemID: ")        end    endend)